import React, { useEffect, useState } from 'react';
import './Tile.css';

function Tile({color, coord, isFocusTile, setFocusTile}) {

  const [isFocused, setIsFocused] = useState(isFocusTile(coord));

  const toggleFocus = () => {
    console.log("tile clicked:", coord[0], coord[1]);
    console.log("is this a focus tile?", isFocused);

    if (isFocused) {
      setFocusTile(null);
      setIsFocused(false);
    }
    else {
      setFocusTile(coord);
      setIsFocused(true);
    }

    // isFocusTile ? setFocusTile(null) : setFocusTile(coord);
  }


  return (
    <div className={`tile 
                    ${color ? 'tile--light' : 'tile--dark'} 
                    ${isFocusTile(coord) ? 'tile--focus' : ''}`}
        onClick={toggleFocus}>

    </div>
  );
}

export default Tile;
