import React, { useEffect, useState } from 'react';
import './Board.scss';
import Tile from '../Tile/Tile';
import { Component } from 'react';

let boardSize = 6;

function Board(props) {

  const [focusTile, setFocusTile] = useState(null);
  const [tiles, setTiles] = useState([])

  // Define tiles with no checkers on them
  const emptyBoard = () => {
    let tiles = []
    for (let i = 0; i < boardSize; i++) {
      for (let j = 0; j < boardSize; j++) {
        // [x, y, hasChecker]
        tiles.push({i, j, hasChecker: null});
      }
    }
    return tiles;
  }

  // Define tiles with start of game checkers arrangement
  const resetBoard = () => {
    let tiles = []
    for (let i = 0; i < boardSize; i++) {
      for (let j = 0; j < boardSize; j++) {
        // [x, y, hasChecker]
        tiles.push({i, j, hasChecker: 'chrome'});
      }
    }
      setTiles(tiles);
  }

  useEffect(() => {
    resetBoard();
  })


  // Check if a tile is currently focused
  const isFocusTile = (coord) => {
    if (!focusTile) 
      return false;

    if ((focusTile[0] == coord[0]) && (focusTile[1] == coord[1]))
      return true;
  }

  // Determines what color a tile will be
  const getColor = (coord) => {
    let i = coord[0]
    let j = coord[1]

    if (((i % 2 == 0) && (j % 2 == 0)) | ((i % 2 == 1) && (j % 2 == 1)))
      return 0;
    return 1;
  }

  return (
    <div className="board">
      {tiles.map((tile, i) => {
        return <Tile 
                key={i}
                coord = {[tile[0], tile[1]]}
                color = {getColor([tile[0], tile[1]])}
                hasChecker = {tile[2]}
                isFocusTile = {isFocusTile}
                setFocusTile = {setFocusTile}
               /> 
      })}
    </div>
  );
}

export default Board;
