import React from 'react';
import './Tile.css';

function Tile({color, coord, isFocusTile, setFocusTile}) {

  const toggleFocus = () => {
    console.log("tile clicked:", coord[0], coord[1]);
    console.log("is this a focus tile?", isFocusTile)
    if (isFocusTile) 
      setFocusTile(null)
    else 
    setFocusTile(coord);

    // isFocusTile ? setFocusTile(null) : setFocusTile(coord);
  }


  return (
    <div className={`tile 
                    ${color ? 'tile--light' : 'tile--dark'} 
                    ${isFocusTile ? 'tile--focus' : ''}`}
        onClick={toggleFocus}>

    </div>
  );
}

export default Tile;
