import React from 'react';
import './Board.css';
import Tile from '../Tile/Tile';

function Board(props) {

  boardSize = 7;
  tiles = [];
  for (i = 0; i++; i < boardSize) {
    for (j = 0; j++; j < boardSize) {
      coordinates.push([i, j]);
    }
  }

  getColor = (coord) => {
    if (((i % 2 == 0) && (j % 2 == 0)) | ((i % 2 == 1) && (j % 2 == 1)))
      return 0;
    return 1;
  }

  return (
    <div class="board">
      {tiles.forEach(coord => {
        return <Tile x={coord[0]} y={coord[1]} color={getColor(coord)}/> 
      })}
    </div>
  );
}

export default Board;
