import styled from "styled-components";

export const Nav = styled.nav`
  background-image: linear-gradient(180deg, rgba(170,170,170,0.4), rgba(255,0,0,0));
  width: 100vw;
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 2rem;

  .title {
    margin: 0;
  }
`;
