import React, { useEffect, useState } from 'react';
import './Board.scss';
import Tile from '../Tile/Tile';
import { Component } from 'react';
import { emptyBoard, resetBoard, getColor } from '../../utils/BoardFunctions';

let boardSize = 8;

function Board(props) {

  const [focusTile, setFocusTile] = useState(null);
  const [tiles, setTiles] = useState([])

  useEffect(() => {
    setTiles(resetBoard(boardSize));
  }, [])

  useEffect(() => {
    console.log("updated Board");
  })

  // Check if a tile is currently focused
  const isFocusTile = (coord) => {
    if (!focusTile) 
      return false;

    if ((focusTile[0] == coord[0]) && (focusTile[1] == coord[1]))
      return true;
  }

  const startTileChosen = (e) => {
    console.log("start on:", e.target.querySelector(".tile--dark"));
  }

  const endTileChosen = (e) => {
    console.log("end on:", e.target.querySelector(".tile--dark"));
  }

  return (
    <div className = "board"
         onMouseDown = {startTileChosen}
         onMouseUp = {endTileChosen}
    >
      {tiles.map((tile, i) => {
        return <Tile 
                key={i}
                coord = {[tile.i, tile.j]}
                color = {getColor([tile.i, tile.j])}
                hasChecker = {tile.hasChecker}
                isFocusTile = {isFocusTile}
                setFocusTile = {setFocusTile}
               /> 
      })}
    </div>
  );
}

export default Board;
