import styled from "styled-components";

export const GameContainer = styled.div`
  display: flex;
  width: 100%;
  justify-content: space-evenly;


`;

export const Score = styled.div`
  .player {
    display: flex;
  }

  .logo {
    height: 50px;
    width: 50px;
  }

  .name {
    margin: 0;
  }
`;
