import styled from "styled-components";

export const Score = styled.div`
  .player {
    display: flex;
    margin-bottom: 50px;
  }

  .logo {
    max-height: 75px;
  }

  .name {
    margin: 0;
  }

  .name-label {
    font-size: 1rem;
    margin-top: 0;
  }
`;
